export function buildCuratedList(cat, opts){
  const list = (PLACES[cat] || []).slice(0, (opts?.max ?? 12));
  const labelOpen = opts?.labelOpen || "Open";
  if (!list.length) return "";
  return list.map(p =>
    `- **${p.name}**${p.tags?.length ? ` — *${p.tags.join(", ")}*` : ""}\n  - [${labelOpen}](${p.url})`
  ).join("\n\n");
}
